   *
   * @param {String} msg message to display
   */
  error: function(msg) {
    this._logger.error(msg);
  }
};

import { Log } from './Log.js';

export default class NetCodec {

  constructor(socket) {
    this._socket = socket;

    this._clientSubscribers = [];
    this._updateListener = null;
    this._RPCListener = null;

    socket.on('message', (raw) => {
      var packet = JSON.parse(raw);
      if (packet.dest === 'client') {
        for (let sub of this._clientSubscribers) {
          sub.cb.call(sub.ctx, packet.type, packet.data);
        }
      } else if (packet.dest === 'update'){
        if (this._updateListener !== null) {
          this._updateListener(packet.data);
        }
      } else if (packet.dest === 'RPC') {
        if (this._RPCListener !== null) {
          this._RPCListener(packet.scene, packet.entity, packet.component, packet.method, packet.data);
        }
      } else {
        throw new Error(`Unknown packet destination: ${packet.dest}`);
      }
    });
  }

  sendClientMessage(type, data) {
    this._socket.send(JSON.stringify({
      dest: 'client',
      type: type,
      data: data
    }));
  }

  onClientMessage(cb, ctx) {
    this._clientSubscribers.push({
      cb: cb,
      ctx: ctx
    });
  }

  sendUpdate(update) {
    this._socket.send(JSON.stringify({
      dest: 'update',
      data: update
    }));
  }

  setUpdateListener(listener) {
    this._updateListener = listener;
  }

  sendRPC(scene, entity, component, method, data) {
    this._socket.send(JSON.stringify({
      dest: 'RPC',
      scene: scene,
      entity: entity,
      component: component,
      method: method,
      data: data
    }));
  }

  onRPC(listener) {
    this._RPCListener = listener;
  }

}

import { Log } from './Log.js';
import game from './Game.js';

class RPC {

  init(codec) {
    this.codec = codec;
    codec.onRPC((scene, entity, component, method, data) => this.onRemote(scene, entity, component, method, data));
  }

  call(component, method, data) {
    this.codec.sendRPC(component.scene.id, component.entity.id, component.id, method, data);
  }

  onRemote(scene, entity, component, method, data) {
    const sc = game.getScene(scene);
    if (sc == null) return;
    const ent = sc.model.getEntity(entity);
    if (ent == null) return;
    const comp = ent.components[component];
    if (comp == null) return;
    comp[method].call(comp, data);
  }
}

export default new RPC();

