/**
 * Rectangle shape sprite.
 *
 * The drawn rectangle has its top-left corner at `transform` coordinates and
 * spans `width` units horizontally and `height` vertically.
 */
export default class RectangleShape extends Shape {

  onCreate() {
    super.onCreate();
    this.createAttribute('width', 20, Types.Float);
    this.createAttribute('height', 10, Types.Float);
  }


  /**
   * Traces a rectangle shape
   *
   * @protected
   * @param  {CanvasRenderingContext2D} ctx
   */
  traceShape(ctx) {
    ctx.moveTo(0, 0);
    ctx.lineTo(0 + this.width, 0);
    ctx.lineTo(0 + this.width, 0 + this.height);
    ctx.lineTo(0, 0 + this.height);
    ctx.closePath();
  }
}

import Types from '../Types.js';
import Sprite from './Sprite.js';


/**
 * Base class for shape sprites
 */
export default class Shape extends Sprite {

  onCreate() {
    super.onCreate();
    this.createAttribute('fillStyle', '#00f', Types.String);
    this.createAttribute('strokeStyle', '#f00', Types.String);
    this.createAttribute('fill', true, Types.Boolean);
    this.createAttribute('stroke', false, Types.Boolean);
  }




  /**
   * traceShape - Trace the shape using ctx.lineTo and co.
   *
   * This method should be overriden by subclasses.
   *
   * @protected
   * @param  {CanvasRenderingContext2D} ctx The context on which to draw
   */
  traceShape(ctx) {}

  draw(ctx) {
    ctx.save();

    ctx.fillStyle = this.fillStyle;
    ctx.strokeStyle = this.strokeStyle;

    ctx.translate(this.transform.x, this.transform.y);
    ctx.rotate(this.transform.theta);

    ctx.beginPath();

    this.traceShape(ctx);

    if (this.fill) ctx.fill();
    if (this.stroke) ctx.stroke();

    ctx.restore();
  }
}

