/* globals document, window */

const CSS_RULEZ = [`
body, html {
  margin: 0;
  padding: 0;
  height: 100%;
}`,
`#savannah-screen {
  width: 100%;
  height: 100%;
  position: absolute;
  background-color: transparent;
}`];

class Graphics {

  constructor() {
    this._initialized = false;
    this._isCanvasSupported = undefined;
    this._ctx = null;
  }

  // Returns true if graphic rendering is available on this platform
  available() {
    if (typeof document === 'undefined' || typeof window === 'undefined') {
      Log.info('No document or window available: graphics will be disabled.');
      return false;
    }
    if (this._isCanvasSupported === undefined) {
      this._isCanvasSupported = this._determineCanvasSupported();
      if (this._isCanvasSupported) {
        Log.info('Canvas is supported :)');
      } else {
        Log.info('Canvas not supported: graphics will be disabled.');
      }
    }
    return this._isCanvasSupported;
  }

  init() {
    if (this._initialized === true) {
      return;
    }

    this._makeCanvas();
    this._addCSSRules(CSS_RULEZ);
    window.addEventListener('resize', () => this.updateCanvasSize());


    this._initialized = true;
  }

  get height() {
    return (typeof window !== 'undefined') ? window.innerHeight : 0;
  }

  get width() {
    return (typeof window !== 'undefined') ? window.innerWidth : 0;
  }

  clear(color = '#fff') {
    this._ctx.fillStyle = color;
    this._ctx.fillRect(0, 0, this._canvas.width, this._canvas.height);
  }

  get ctx() {
    return this._ctx;
  }

  _makeCanvas() {
    this._canvas = document.createElement('canvas');
    this._canvas.id = 'savannah-screen';
    this._ctx = this._canvas.getContext('2d');
    document.body.appendChild(this._canvas);
    this.updateCanvasSize();
  }

  _determineCanvasSupported() {
    var elem = document.createElement('canvas');
    return !!(elem.getContext && elem.getContext('2d'));
  }

  _addCSSRules(rules) {
    var sheet = window.document.styleSheets[0];
    if (typeof sheet === 'undefined') {
      // The document contains no styleSheet
      document.head.appendChild(document.createElement('style'));
      // Try again now
      sheet = window.document.styleSheets[0];
    }

    for (let rule of rules) {
      if (typeof sheet.insertRule === 'function') {
        sheet.insertRule(rule, sheet.cssRules.length);
      } else {
        sheet.addRule(rule, -1);
      }
    }
  }

  updateCanvasSize() {
    this._canvas.width = window.innerWidth;
    this._canvas.height = window.innerHeight;
  }



}

export default new Graphics();

import System from '../System.js';
import { Log } from '../Log.js';

export default class GraphicSystem extends System {

  constructor() {
    super();
    this._sprites = [];
  }

  addSprite(sprite) {
    this._sprites.push(sprite);
    Log.info(`Graphics: push sprite (length: ${this._sprites.length})`);
  }

  removeSprite(sprite) {
    const idx = this._sprites.indexOf(sprite);
    if (idx < 0) {
      Log.warn('Removing an sprite which is not in graphics system');
      return;
    }
    this._sprites.splice(idx, 1);
    Log.info(`Graphics: pop sprite (length: ${this._sprites.length})`);
  }

  get sprites() {
    return this._sprites;
  }
}

import System from '../System.js';
import { Log } from '../Log.js';

export default class PhysicSystem extends System {

  constructor() {
    super();
    this._obj = [];
    this.buoyancy = 0.1;
    this.gravity = 0;
    this._boundaries = {
      left: -Infinity,
      right: Infinity,
      top: -Infinity,
      bottom: Infinity
    };
  }

  addObject(obj) {
    this._obj.push(obj);
    Log.info(`Physics: push object (length: ${this._obj.length})`);
  }

  removeObject(obj) {
    const idx = this._obj.indexOf(obj);
    if (idx < 0) {
      Log.warn('Removing an object which is not in physcis system');
      return;
    }
    this._obj.splice(idx, 1);
    Log.info(`Physics: pop object (length: ${this._obj.length})`);
  }

  get boundaries() {
    return this._boundaries;
  }

  set boundaries(b) {
    if (b.left != null) this._boundaries.left = b.left;
    if (b.right != null) this._boundaries.right = b.right;
    if (b.top != null) this._boundaries.top = b.top;
    if (b.bottom != null) this._boundaries.bottom = b.bottom;
  }

  onUpdate(dt) {
    // Update all objects positions
    for (const obj of this._obj) {
      obj.transform.x += obj.vx * dt;
      obj.transform.y += obj.vy * dt;
      obj.transform.theta += obj.vtheta * dt;
    }

    // Naïve collision detection
    const objLength = this._obj.length;
    let i=0, j=0;
    for (i = 0 ; i < objLength ; i++){
      const o1 = this._obj[i];
      // Test collision with terrain boundaries
      if (   o1.transform.x - o1.radius <= this._boundaries.left
          || o1.transform.x + o1.radius >= this._boundaries.right
          || o1.transform.y - o1.radius <= this._boundaries.top
          || o1.transform.y + o1.radius >= this._boundaries.bottom) {
        // If it is outside the terrain, adjust position and trigger collision
        const oldX = o1.transform.x;
        const oldY = o1.transform.y;

        o1.transform.x = Math.max(
            Math.min(o1.transform.x, this._boundaries.right - o1.radius),
            this._boundaries.left + o1.radius);
        o1.transform.y = Math.max(
            Math.min(o1.transform.y, this._boundaries.bottom - o1.radius),
            this._boundaries.top + o1.radius);

        o1.entity.triggerEvent('collision', {
          vx: (oldX != o1.transform.x) ? o1.vx : 0,
          vy: (oldY != o1.transform.y) ? o1.vy : 0,
          other: null
        });
      } else {
        // Otherwise (if still in terrain), test collision with other objects
        for (j = i+1 ; j < objLength ; j++) {
          const o2 = this._obj[j];
          if (o1.transform.distanceTo(o2.transform) < o1.radius + o2.radius) {
            o1.entity.triggerEvent('collision', {
              vx: o1.vx - o2.vx,
              vy: o1.vy - o2.vy,
              other: o2
            });
            o2.entity.triggerEvent('collision', {
              vx: o2.vx - o1.vx,
              vy: o2.vy - o1.vy,
              other: o1
            });
          }
        }
      }
    }
  }
}

function s4() {
  return Math.floor((1 + Math.random()) * 0x10000)
    .toString(16)
    .substring(1);
}

export function uid() {
  return s4()+s4()+s4()+s4();
}
